#include "OOLC.h"

namespace nongmaoshichang_NPC {
    class shang : public NPC {
    public:
        int age;
        const char* s;
        point begin;
        point end;
        shang(const char* name, const point& pos, const char* s, int age = 0) :NPC(1, pos, { 0,1 }), begin(pos), end(pos + point(0, 4)), age(age), s(s) { this->name = name; ifMove = 1; }
        virtual void playerDo(player* p, Map* m) {
            using namespace std;
            say(s);
            sayyes();
            sayclear();
        }
        virtual void selfDo(player* p, Map* m) {
            age++, age %= 30;
            if (age <= 1) {
                ifMove = 1;
            }
            else {
                ifMove = 0;
            }
            if (position == begin) {
                velocity = point(0, 1);
            }
            else if (position == end) {
                velocity = point(0, -1);
            }
        }
        virtual ~shang() {}
    };
}

static void sceneReady(scene& scn, player& p);
static void sceneEnd(scene& scn);
void nongmaoshichang(player& p) {
	scene nongmaoshichang;
	sceneReady(nongmaoshichang, p);
	nongmaoshichang.gameStart();
    //动画
    try {
        while (1) {
            nongmaoshichang.printMap();
 
            nongmaoshichang.playerDo();//其他按键功能
            nongmaoshichang.NPCDo();
            nongmaoshichang.playerMove();
            nongmaoshichang.NPCMove();
            nongmaoshichang.esc();

            Sleep(50);
        }
    }
    catch (int) {//换地图，地图文件存档

    }
    catch (const char*) {//退出
        p.exit = 1;
    }
    sceneEnd(nongmaoshichang);
}

