#include "OOLC.h"
#include <string>

static void sceneReady(scene& scn, player& p);
static void sceneEnd(scene& scn);


namespace shatan_NPC {//NPC
    class shu :public NPC {
    public:
        int age;

        shu() :NPC(1, { 33,5 }, { 1,0 }, 0), age(0) { name = "鼠", passable = 1; }
        virtual void selfDo(player* p, Map* m = nullptr) {
            if (age == 0) {
                if (p->position == point(33, 6)) {
                    age++;
                    say("鼠：这里水很深，你听鼠的，你把握不住。");
                    sayyes();
                    sayclear();
                }
            }
            if (age == 1) {
                crazyMove = 1;
                if (position == point(39, 5)) {
                    visible = 0;
                    age++;
                }
            }
            if (age == 2) {
                crazyMove = 0;
                age++;
            }
        }
        virtual ~shu() {}
    };
    class niao :public NPC {
    public:
        int age;
        int run;
        niao(const char* name, const point& position) :NPC(1, position), age(0), run(0) { this->name = name; }
        virtual void selfDo(player* p, Map* m = nullptr) {
            if (age == 1) {
                if (position.x <= 0 || position.y <= 0) {
                    visible = 0;
                    crazyMove = 0;
                    age++;
                }
                run = !run;
                velocity = {-run, -1};
            }
        }
        virtual void playerDo(player* p, Map* m = nullptr) {
            age++;
            crazyMove = 1;
            say((std::string(name) + "：来点薯条！").c_str());
            sayyes();
            sayclear();

            say("你的薯条被叼走了！");
            sayyes();
            sayclear();

        }
        virtual ~niao() {}
    };
}


void shatan(player& p) {
	scene shatan;
	sceneReady(shatan, p);
	shatan.gameStart();
    //动画
    try {
        while (1) {
            shatan.printMap();
 
            shatan.playerDo();//其他按键功能
            shatan.NPCDo();
            shatan.playerMove();
            shatan.NPCMove();
            shatan.esc();

            Sleep(50);
        }
    }
    catch (int) {//换地图，地图文件存档

    }
    catch (const char*) {//退出
        p.exit = 1;
    }
    sceneEnd(shatan);
}

